mapper.select:
SELECT A.XH, A.YWLX, A.YWZLX,A.RWBH, A.RWNR, A.SJKSSJ, A.RWCSYY, A.YQBJSJ, A.SFCQ, A.FZJG, A.BLR, A.CJSJ, A.CJR, A.XGSJ, A.XGR, A.BJQK,A.FKR ,A.FKSJ ,A.FKNR ,A.SFBJ, TO_CHAR(A.SJKSSJ, 'yyyy-MM-dd HH24:mi:ss') BJSJ, A.TYPE, C.YHMC, B.ZZJC FROM (SELECT * FROM T_GZRW_JBXX WHERE YWLX IN ('DQYJ','DBSYJ','WRYZXYJ')) A LEFT JOIN U_WRYDTGL_XC.T_ADMIN_RMS_ZZJG B ON A .FZJG = B.ZZBH LEFT JOIN U_WRYDTGL_XC.T_ADMIN_RMS_YH C ON A .BLR = C.YHID WHERE 1=1
AND A.CJSJ >= to_date(#{STARTTIME},'yyyy-mm-dd,hh24:mi:ss')
AND A.CJSJ <= to_date(#{ENDTIME},'yyyy-mm-dd,hh24:mi:ss')
AND A.YWLX = #{YWLX}
AND A.FZJG = #{FZJG}
AND A.BJQK = #{BJQK}
AND A.SFCQ = #{SFCQ}
AND (A.RWNR LIKE #{search} OR B.ZZJC LIKE #{search} OR C.YHMC LIKE #{search})
AND C.XTZH = #{YHID}

SELECT XH, YQBJSJ, ROUND (TO_NUMBER(SYSDATE - YQBJSJ)) AS OVERTIME FROM T_GZRW_JBXX WHERE ROUND(TO_NUMBER(SYSDATE - SJKSSJ) * 24 * 60 * 60) >ROUND(TO_NUMBER(YQBJSJ - SJKSSJ) * 24 * 60 * 60) AND BJQK = '0'
AND CJSJ >= to_date(#{STARTTIME},'yyyy-mm-dd,hh24:mi:ss')
AND CJSJ <= to_date(#{ENDTIME},'yyyy-mm-dd,hh24:mi:ss')
AND YWLX = #{YWLX}
AND FZJG = #{FZJG}

mapper.update:
UPDATE T_GZRW_LZXX SET XH=XH 

,BLR=#{BLR}


,XBBLR=#{XBBLR}


,FKNR=#{FKNR}


,FKSJ=#{FKSJ}


,FKZT=#{FKZT}

WHERE XH = #{XH}
